import React, { useEffect, useState } from 'react';
import { useUser } from '../contexts/UserContext';
import { getCurrentLocation } from '../services/locationService';

const HomePage = () => {
  const { user, logout } = useUser();
  const [location, setLocation] = useState(user.location);
  const [messages, setMessages] = useState([]);
  const [messageText, setMessageText] = useState('');

  useEffect(() => {
    const fetchLocation = async () => {
      try {
        const newLocation = await getCurrentLocation();
        setLocation(newLocation);
        // Optionally update user location in your backend or state as needed
      } catch (error) {
        console.error('Error fetching location:', error);
      }
    };

    fetchLocation(); // Initial fetch
    const interval = setInterval(fetchLocation, 30000); // Update every 30 seconds

    return () => clearInterval(interval);
  }, []);

  const handleSendMessage = (e) => {
    e.preventDefault();
    const newMessage = {
      text: messageText,
      location: location,
      user: user.username,
      timestamp: new Date(),
    };
    setMessages([...messages, newMessage]);
    setMessageText('');
  };

  return (
    <div>
      <h1>Welcome, {user.username}</h1>
      <p>Current Location: {location.latitude}, {location.longitude}</p>
      <button onClick={logout}>Logout</button>

      <form onSubmit={handleSendMessage}>
        <input
          type="text"
          value={messageText}
          onChange={(e) => setMessageText(e.target.value)}
          placeholder="Type your message"
          required
        />
        <button type="submit">Send</button>
      </form>

      <div>
        <h2>Messages</h2>
        {messages.map((msg, index) => (
          <div key={index}>
            <p>{msg.user}: {msg.text}</p>
            <p>Location: {msg.location.latitude}, {msg.location.longitude}</p>
            <p>Time: {msg.timestamp.toLocaleString()}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
