// UserContext.jsx

import React, { createContext, useContext, useState } from 'react';

const UserContext = createContext();

const UserProvider = ({ children }) => {
  const [user, setUser] = useState(null);

  const login = (email, password) => {
    // Your login logic here
    console.log('Login function called with email:', email, 'and password:', password);
    setUser({ email }); // This is a placeholder. Replace with actual user data after authentication.
  };

  const signup = (username, email, password, location) => {
    // Your signup logic here
    console.log('Signup function called with username:', username, 'email:', email, 'password:', password, 'and location:', location);
    setUser({ username, email, location }); // This is a placeholder. Replace with actual user data after signup.
  };

  return (
    <UserContext.Provider value={{ user, setUser, login, signup }}>
      {children}
    </UserContext.Provider>
  );
};

const useUser = () => useContext(UserContext);

export { UserProvider, useUser };
