import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getLocationPermission, getCurrentLocation } from '../utils/locationUtils'; // Update the path as necessary
import '../styles/login.css';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [username, setUsername] = useState('');
  const [isSignup, setIsSignup] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      const response = await fetch('http://localhost:9000/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();
      if (!data.success) {
        throw new Error(data.error || 'Login failed');
      }

      console.log('Login successful:', data);
      navigate('/home');
    } catch (error) {
      console.error('Error during login:', error);
      alert('Login failed. Please check your credentials and try again.');
    }
  };

  const handleSignUp = async () => {
    try {
      const permissionGranted = await getLocationPermission();
      if (!permissionGranted) {
        console.error('Location permission denied.');
        return;
      }

      const location = await getCurrentLocation();

      const response = await fetch('http://localhost:9000/api/users/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, email, password, location }),
      });

      const data = await response.json();
      if (!data.success) {
        throw new Error(data.error || 'Signup failed');
      }

      console.log('Signup successful:', data);
      navigate('/home');
    } catch (error) {
      console.error('Error during signup:', error);
      alert('Signup failed. Please try again.');
    }
  };

  return (
    <div className="login-container">
      <h2 className="login-header">{isSignup ? 'Sign Up' : 'Login'}</h2>
      {isSignup && (
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          className="login-input"
        />
      )}
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        className="login-input"
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        className="login-input"
      />
      <button className="login-button" onClick={isSignup ? handleSignUp : handleLogin}>
        {isSignup ? 'Sign Up' : 'Login'}
      </button>
      <button className="toggle-button" onClick={() => setIsSignup(!isSignup)}>
        {isSignup ? 'Have an account? Login' : 'Don\'t have an account? Sign Up'}
      </button>
    </div>
  );
};

export default LoginPage;
