import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const SignUpPage = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();


  const handleSignup = async () => {
    try {
      console.log('Attempting signup...');
      const location = await getCurrentLocation();
      console.log('Location obtained:', location);

      const response = await fetch('http://localhost:9000/api/users/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, email, password, location }), // Include location in the request body
      });

      const data = await response.json();
      console.log('Signup response:', data);

      if (data.success) {
        localStorage.setItem('token', data.token); // Store token in localStorage
        localStorage.setItem('userId', data.user.id); // Store userId in localStorage
        console.log('Signup successful. Redirecting...');
        navigate('/'); 
      } else {
        console.log('Signup failed.');
        alert('Signup failed');
      }
    } catch (error) {
      console.error('Error during signup:', error);
      alert('An error occurred during signup: ' + error.message);
    }
  };




  const getCurrentLocation = async () => {
    try {
      return new Promise((resolve, reject) => {
        if (navigator.geolocation) {
          navigator.geolocation.getCurrentPosition(
            (position) => {
              resolve({
                type: 'Point',
                coordinates: [position.coords.longitude, position.coords.latitude],
              });
            },
            (error) => reject(error)
          );
        } else {
          reject(new Error('Geolocation is not supported by this browser.'));
        }
      });
    } catch (error) {
      console.error('Error in getCurrentLocation:', error);
      throw error; // Rethrow the error to propagate it upwards
    }
  };

  return (
    <div>
      <h2>Signup</h2>
      <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} placeholder="Username" />
      <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" />
      <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" />
      <button onClick={handleSignup}>Signup</button>
    </div>
  );
};

export default SignUpPage;
