import React, { useState, useEffect } from 'react';
import '../styles/home.css';

const HomePage = () => {
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchMessages();
    }, []);

    useEffect(() => {
        // Log location data for each message
        messages.forEach((msg, index) => {
            console.log(`Message ${index} Location:`, msg.location);
        });
    }, [messages]); // Run this effect whenever messages change

    const fetchMessages = async () => {
        try {
            const response = await fetch('http://localhost:9000/api/messages');
            if (!response.ok) {
                throw new Error('Failed to fetch messages');
            }
            const data = await response.json();
            setMessages(data.reverse());
            setLoading(false);
        } catch (error) {
            console.error('Failed to fetch messages:', error);
            setError(error.message);
            setLoading(false);
        }
    };

    const handleKeyDown = async (event) => {
        if (event.key === 'Enter') {
            await sendMessage();
        }
    };

    const sendMessage = async () => {
        if (message.trim() !== '') {
            try {
                const response = await fetch('http://localhost:9000/api/messages/send', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ text: message })
                });
                const data = await response.json();
                console.log('Success:', data);
                if (data.success) {
                    setMessages([data.message, ...messages]);
                }
                setMessage('');
            } catch (error) {
                console.error('Error sending message:', error);
            }
        }
    };

    return (
        <div>
            <h2>Home Page</h2>
            <div>
                <input
                    type="text"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    placeholder="Type your message..."
                    onKeyDown={handleKeyDown}
                />
                <button onClick={sendMessage}>Send</button>
            </div>
            <div>
                {loading ? (
                    <p>Loading...</p>
                ) : error ? (
                    <p>Error: {error}</p>
                ) : (
                    messages.map((msg, index) => (
                        <div key={index}>
                            <p>{msg.text}</p>
                            {/* Rendering location */}
                            {msg.location && (
                                <p>Location: {msg.location.latitude}, {msg.location.longitude}</p>
                            )}
                        </div>
                    ))
                )}
            </div>
        </div>
    );
};

export default HomePage;
