import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const SignUpPage = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [location, setLocation] = useState(null);
  const navigate = useNavigate();

  const getCurrentLocation = async () => {
    return new Promise((resolve, reject) => {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const location = {
            type: 'Point',
            coordinates: [position.coords.longitude, position.coords.latitude],
          };
          resolve(location);
        },
        (error) => reject(error)
      );
    });
  };

  const handleSignup = async () => {
    try {
      console.log('Attempting signup...');
      const location = await getCurrentLocation();
      setLocation(location);
      console.log('Location obtained:', location);

      const response = await fetch('http://localhost:9000/api/users/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, email, password, location }),
      });

      const data = await response.json();
      console.log('Signup response:', data);

      if (data.success && data.token) {
        localStorage.setItem('token', data.token); // Store token in localStorage
        console.log('Stored token:', localStorage.getItem('token')); // Debug log to verify token is stored
        localStorage.setItem('userId', data.user.id); // Store userId in localStorage
        console.log('Stored userId:', localStorage.getItem('userId')); // Debug log to verify userId is stored
        console.log('Signup successful. Redirecting...');
        navigate('/');
      } else {
        console.log('Signup failed or token missing.');
        alert('Signup failed or token missing.');
      }
    } catch (error) {
      console.error('Error during signup:', error);
      alert('An error occurred during signup: ' + error.message);
    }
  };

  return (
    <div>
      <h1>Sign Up</h1>
      <input
        type="text"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleSignup}>Sign Up</button>
    </div>
  );
};

export default SignUpPage;
