import React, { useRef, useEffect } from 'react';
import '../styles/SearchPlaceComponent.css';

const SearchPlaceComponent = ({ setDestination }) => {
  const searchBoxRef = useRef(null);

  useEffect(() => {
    if (!searchBoxRef.current) return;

    const initializeAutocomplete = () => {
      const autocomplete = new window.google.maps.places.Autocomplete(searchBoxRef.current);
      autocomplete.addListener('place_changed', async () => {
        const place = autocomplete.getPlace();
        if (place.geometry) {
          const location = {
            latitude: place.geometry.location.lat(),
            longitude: place.geometry.location.lng(),
          };

          // Fetch additional place details if needed
          const placeDetails = await fetchPlaceDetails(place.place_id);
          location.details = placeDetails;

          setDestination(location);
        }
      });
    };

    if (window.google && window.google.maps && window.google.maps.places) {
      initializeAutocomplete();
    }
  }, [setDestination]);

  const fetchPlaceDetails = async (placeId) => {
    const apiKey = 'AIzaSyDHwPZ-34X-zriMdx_Wz_RIYk43H4sAeGU';
    const url = `https://maps.googleapis.com/maps/api/place/details/json?place_id=${placeId}&key=${apiKey}`;
    const response = await fetch(url);
    const data = await response.json();
    return data.result;
  };

  return (
    <input
      className="search-place-input"
      type="text"
      placeholder="Search for a place"
      ref={searchBoxRef}
    />
  );
};

export default SearchPlaceComponent;
