import React, { useState, useEffect } from 'react';
import { useUser } from '../contexts/UserContext';
import { useNavigate } from 'react-router-dom';

const HomePage = () => {
  const { user, setUser } = useUser();
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Redirect to login only if user becomes null (after logout)
    if (!user) {
      navigate('/login');
    }
  }, [user, navigate]);

  // ... other useEffect hooks and functions (unchanged)

  const handleLogout = async () => {
    try {
      setUser(null);
      localStorage.removeItem('userId');
      // Optionally, send a logout request to the server (if applicable)
      // ...
      // Redirect to login only after logout
      navigate('/login');
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  // ... other functions (unchanged)

  return (
    <div className="container">
      <h2 className="home-header">Home Page</h2>
      <div className="message-input-container">
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Type your message..."
          onKeyDown={handleKeyDown}
        />
        <button onClick={sendMessage}>Send</button>
      </div>
      <button onClick={handleLogout}>Logout</button>

      {loading && <p>Loading...</p>}
      {error && <p>{error}</p>}
      <div className="message-list">
        {messages.map((msg, index) => (
          <div key={index} className="message-item">
            <p>{msg.text}</p>
            {msg.location && (
              <p>
                Location: Latitude {msg.location.latitude}, Longitude {msg.location.longitude}
              </p>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
