import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/signup.css';

const SignupPage = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSignup = async () => {
    try {
      const locationPermission = await getLocationPermission(); // Request location permission
      if (locationPermission) {
        const location = await getCurrentLocation(); // Get user's current location
        const signupData = {
          username,
          email,
          password,
          location
        };
        const response = await fetch('http://localhost:9000/api/users/register', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(signupData)
        });
        const data = await response.json();
        if (!data.success) {
          throw new Error(data.error || 'Signup failed');
        }
        console.log('Signup successful:', data);
        navigate('/home');
      } else {
        console.log('Location permission denied.');
        alert('Location permission is required to sign up.');
      }
    } catch (error) {
      console.error('Error registering user:', error);
      alert('Signup failed. Please ensure location access is enabled and try again.');
    }
  };

  const getLocationPermission = async () => {
    return new Promise((resolve) => {
      if (navigator.permissions) {
        navigator.permissions.query({ name: 'geolocation' }).then((permissionStatus) => {
          resolve(permissionStatus.state === 'granted');
        });
      } else if ('geolocation' in navigator) {
        resolve(true); // Geolocation API is supported, assume permission is granted
      } else {
        resolve(false); // Geolocation API is not supported
      }
    });
  };

  const getCurrentLocation = async () => {
    return new Promise((resolve, reject) => {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          resolve({
            type: 'Point',
            coordinates: [position.coords.longitude, position.coords.latitude]
          });
        },
        (error) => {
          console.error('Error getting location:', error);
          reject(error);
        }
      );
    });
  };

  return (
    <div className="signup-container">
      <h2>Sign Up</h2>
      <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
      <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
      <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
      <button onClick={handleSignup}>Sign Up</button>
    </div>
  );
};

export default SignupPage;
