import { useEffect, useState } from 'react';
import { useUser } from '../contexts/UserContext';

const LocationUpdater = () => {
  const { userId } = useUser();
  const [prevLocation, setPrevLocation] = useState(null);

  useEffect(() => {
    const updateLocation = async (latitude, longitude) => {
      try {
        const response = await fetch(`http://localhost:9000/api/users/${userId}/location`, {
          method: 'PATCH',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ latitude, longitude }),
        });

        if (response.ok) {
          console.log('Updated Location');
        } else {
          console.error('Failed to update location');
        }
      } catch (error) {
        console.error('Error updating location:', error);
      }
    };

    const checkLocation = () => {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition((position) => {
          const { latitude, longitude } = position.coords;
          const newLocation = { latitude, longitude };

          if (!prevLocation || (prevLocation.latitude !== latitude || prevLocation.longitude !== longitude)) {
            console.log(`Updating location to: (${latitude}, ${longitude})`);
            updateLocation(latitude, longitude);
            setPrevLocation(newLocation);
          } else {
            console.log('Location has not changed');
          }
        }, (error) => {
          console.error('Error getting location:', error);
        });
      }
    };

    const intervalId = setInterval(checkLocation, 10000); // Check location every 10 seconds for testing

    return () => clearInterval(intervalId); // Cleanup on component unmount
  }, [userId, prevLocation]);

  return null;
};

export default LocationUpdater;
