// Frontend (React)
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const HomePage = () => {
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchMessages();
    }, []);

    const fetchMessages = async () => {
        try {
            const response = await axios.get('http://localhost:9000/api/messages');
            setMessages(response.data.reverse());
            setLoading(false);
        } catch (error) {
            console.error('Error fetching messages:', error);
            setError(error.message);
            setLoading(false);
        }
    };

    const handleKeyDown = async (event) => {
        if (event.key === 'Enter') {
            await sendMessage();
        }
    };

    const getCurrentLocation = async () => {
        return new Promise((resolve, reject) => {
            if (navigator.geolocation) {
                navigator.geolocation.getCurrentPosition(
                    (position) => {
                        resolve({
                            coordinates: [position.coords.latitude, position.coords.longitude]
                        });
                    },
                    (error) => {
                        reject(error);
                    }
                );
            } else {
                reject(new Error('Geolocation is not supported by this browser.'));
            }
        });
    };

    const sendMessage = async () => {
        if (message.trim() !== '') {
            try {
                const location = await getCurrentLocation();
                const messageObject = {
                    text: message,
                    location: location
                };
                const response = await axios.post('http://localhost:9000/api/messages/send', messageObject);
                const data = response.data;
                if (data.success) {
                    setMessages([data.message, ...messages]);
                }
                setMessage('');
            } catch (error) {
                console.error('Error sending message:', error);
            }
        }
    };

    return (
        <div>
            <h2>Home Page</h2>
            <div>
                <input
                    type="text"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    placeholder="Type your message..."
                    onKeyDown={handleKeyDown}
                />
                <button onClick={sendMessage}>Send</button>
            </div>
            <div>
                {loading ? (
                    <p>Loading...</p>
                ) : error ? (
                    <p>Error: {error}</p>
                ) : (
                    messages.map((msg, index) => (
                        <div key={index}>
                            <p>{msg.text}</p>
                            {msg.location && msg.location.coordinates && (
                                <p>Location: {msg.location.coordinates[0]}, {msg.location.coordinates[1]}</p>
                            )}
                        </div>
                    ))
                )}
            </div>
        </div>
    );
};

export default HomePage;
