import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import LoginPage from './components/LoginPage';
import HomePage from './components/HomePage';
import SignUpPage from './components/SignUpPage';
import LocationUpdater from './components/LocationUpdater';
import { useUser } from './contexts/UserContext'; // Adjust path if needed

function App() {
  const { userId, setUserId } = useUser(); // Access userId and setUserId from context

  return (
    <Router>
      {userId && <LocationUpdater userId={userId} />} {/* Render LocationUpdater only if userId is set */}
      <Routes>
        <Route path="/" element={<LoginPage setUserId={setUserId} />} /> {/* Pass setUserId to LoginPage */}
        <Route path="/home" element={<HomePage />} />
        <Route path="/signup" element={<SignUpPage />} />
      </Routes>
    </Router>
  );
}

export default App;
