import React, { useEffect, useState } from 'react';
import { useUser } from '../contexts/UserContext';
import { getCurrentLocation } from '../services/locationService';

const HomePage = () => {
  const { user, logout } = useUser();
  const [location, setLocation] = useState(user.location);

  useEffect(() => {
    const fetchLocation = async () => {
      try {
        const newLocation = await getCurrentLocation();
        setLocation(newLocation);
        // Optionally update user location in your backend or state as needed
      } catch (error) {
        console.error('Error fetching location:', error);
      }
    };

    fetchLocation(); // Initial fetch
    const interval = setInterval(fetchLocation, 30000); // Update every 30 seconds

    return () => clearInterval(interval);
  }, []);

  return (
    <div>
      <h1>Welcome, {user.username}</h1>
      <p>Current Location: {location.latitude}, {location.longitude}</p>
      <button onClick={logout}>Logout</button>
      {/* Messages can be added here later if needed */}
    </div>
  );
};

export default HomePage;
