import React, { useRef, useEffect } from 'react';
import usePlacesAutocomplete, { getGeocode, getLatLng } from 'use-places-autocomplete';
import {
  Combobox,
  ComboboxInput,
  ComboboxPopover,
  ComboboxList,
  ComboboxOption,
} from '@reach/combobox';
import '@reach/combobox/styles.css';

const SearchPlaceComponent = ({ setDestination, mapRef }) => {
  const {
    ready,
    value,
    suggestions: { status, data },
    setValue,
    clearSuggestions,
  } = usePlacesAutocomplete();

  const handleInput = (e) => {
    setValue(e.target.value);
  };

  const handleSelect = async (address) => {
    setValue(address, false);
    clearSuggestions();

    try {
      const results = await getGeocode({ address });
      const { lat, lng } = await getLatLng(results[0]);
      const northeast = results[0].geometry.viewport.getNorthEast();
      const southwest = results[0].geometry.viewport.getSouthWest();

      const boundary = {
        northeast: { lat: northeast.lat(), lng: northeast.lng() },
        southwest: { lat: southwest.lat(), lng: southwest.lng() },
      };

      setDestination({
        address,
        latitude: lat,
        longitude: lng,
        boundary,
      });

      if (mapRef.current) {
        const map = new window.google.maps.Map(mapRef.current, {
          center: { lat, lng },
          zoom: 14,
        });

        new window.google.maps.Marker({
          position: { lat, lng },
          map,
          title: address,
        });

        new window.google.maps.Rectangle({
          bounds: {
            north: boundary.northeast.lat,
            south: boundary.southwest.lat,
            east: boundary.northeast.lng,
            west: boundary.southwest.lng,
          },
          map,
          strokeColor: '#FF0000',
          strokeOpacity: 0.8,
          strokeWeight: 2,
          fillColor: '#FF0000',
          fillOpacity: 0.35,
        });
      }
    } catch (error) {
      console.log('Error: ', error);
    }
  };

  return (
    <div>
      <Combobox onSelect={handleSelect}>
        <ComboboxInput
          value={value}
          onChange={handleInput}
          disabled={!ready}
          placeholder="Search for a place"
        />
        <ComboboxPopover>
          {status === 'OK' && (
            <ComboboxList>
              {data.map(({ place_id, description }) => (
                <ComboboxOption key={place_id} value={description} />
              ))}
            </ComboboxList>
          )}
        </ComboboxPopover>
      </Combobox>
    </div>
  );
};

export default SearchPlaceComponent;
