import React, { useState, useRef, useEffect } from 'react';
import '../styles/SearchPlaceComponent.css';

const SearchPlaceComponent = ({ setDestination }) => {
  const [autocomplete, setAutocomplete] = useState(null);
  const inputRef = useRef(null);
  const mapRef = useRef(null);
  const markerRef = useRef(null);
  const polygonRef = useRef(null);

  useEffect(() => {
    if (!window.google || !window.google.maps) {
      console.error('Google Maps JavaScript API library must be loaded.');
      return;
    }

    const initializeAutocomplete = () => {
      const input = inputRef.current;
      const autocomplete = new window.google.maps.places.Autocomplete(input);
      autocomplete.setFields(['geometry', 'address_components', 'place_id']);
      autocomplete.addListener('place_changed', () => onPlaceChanged(autocomplete));
      setAutocomplete(autocomplete);
    };

    initializeAutocomplete();
  }, []);

  const onPlaceChanged = async (autocomplete) => {
    const place = autocomplete.getPlace();

    if (!place.geometry) {
      console.error('Place contains no geometry');
      return;
    }

    const location = place.geometry.location;
    const placeId = place.place_id;

    try {
      const placeDetailsResponse = await fetch(`https://maps.googleapis.com/maps/api/place/details/json?place_id=${placeId}&key=AIzaSyDHwPZ-34X-zriMdx_Wz_RIYk43H4sAeGU`);
      const placeDetails = await placeDetailsResponse.json();
      const radius = placeDetails.result.geometry.viewport ? calculateRadius(placeDetails.result.geometry.viewport) : 1000;

      setDestination({
        latitude: location.lat(),
        longitude: location.lng(),
        radius: radius,
        viewport: {
          north: place.geometry.viewport.getNorthEast().lat(),
          east: place.geometry.viewport.getNorthEast().lng(),
          south: place.geometry.viewport.getSouthWest().lat(),
          west: place.geometry.viewport.getSouthWest().lng(),
        },
      });

      const map = new window.google.maps.Map(mapRef.current, {
        center: location,
        zoom: 15,
      });

      if (markerRef.current) {
        markerRef.current.setMap(null);
      }

      const marker = new window.google.maps.Marker({
        position: location,
        map: map,
      });
      markerRef.current = marker;

      if (polygonRef.current) {
        polygonRef.current.setMap(null);
      }

      const circle = new window.google.maps.Circle({
        center: location,
        radius: radius,
        strokeColor: '#FF0000',
        strokeOpacity: 0.8,
        strokeWeight: 2,
        fillColor: '#FF0000',
        fillOpacity: 0.35,
      });

      circle.setMap(map);
      polygonRef.current = circle;

    } catch (error) {
      console.error('Error fetching place details:', error);
    }
  };

  const calculateRadius = (viewport) => {
    const ne = viewport.getNorthEast();
    const sw = viewport.getSouthWest();
    const latDiff = ne.lat() - sw.lat();
    const lngDiff = ne.lng() - sw.lng();
    return Math.sqrt(Math.pow(latDiff, 2) + Math.pow(lngDiff, 2)) * 111000 / 2; // approximate conversion to meters
  };

  return (
    <div className="search-place-container">
      <input
        type="text"
        ref={inputRef}
        className="search-place-input"
        placeholder="Search for a place"
      />
      <div id="map" ref={mapRef} className="map-container"></div>
    </div>
  );
};

export default SearchPlaceComponent;
