import React, { useState, useEffect, useCallback } from 'react';
import { useUser } from '../contexts/UserContext';
import { useNavigate } from 'react-router-dom';
import '../styles/HomePage.css';
import MessageInputComponent from './MessageInputComponent';
import SearchPlaceComponent from './SearchPlaceComponent';
import MessageListComponent from './MessageListComponent';
import LogoutButtonComponent from './LogoutButtonComponent';
import EditMessageComponent from './EditMessageComponent';

const HomePage = () => {
  const { setUser } = useUser();
  const [user, setUserState] = useState(null);
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [destination, setDestination] = useState(null);
  const navigate = useNavigate();
  const [usersInBoundary, setUsersInBoundary] = useState([]);
  const [editingMessage, setEditingMessage] = useState(null); 

  const updateLocation = useCallback(async () => {
    const userId = localStorage.getItem('userId');
    if (!userId) {
      console.error('No user ID found in localStorage');
      return;
    }

    try {
      const { latitude, longitude } = await getCurrentLocation();
      console.log('Updating location for user:', userId);
      const response = await fetch(`http://localhost:9000/api/users/${userId}/location`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ latitude, longitude }),
      });

      if (response.ok) {
        console.log('Location updated');
      } else {
        console.error('Failed to update location');
      }
    } catch (error) {
      console.error('Error updating location:', error);
    }
  }, []);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
    } else {
      fetch('http://localhost:9000/api/auth/validate-token', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      })
        .then(response => response.json())
        .then(data => {
          if (data.success) {
            setUser({ id: data.user.id, username: data.user.username });
            localStorage.setItem('userId', data.user.id);
            setUserState({ id: data.user.id, username: data.user.username });
            setLoading(false);
          } else {
            localStorage.removeItem('token');
            localStorage.removeItem('userId');
            navigate('/login');
          }
        })
        .catch(() => {
          localStorage.removeItem('token');
          localStorage.removeItem('userId');
          navigate('/login');
        });
    }
  }, [navigate, setUser]);

  useEffect(() => {
    fetchMessages();
  }, []);

  useEffect(() => {
    messages.forEach((msg, index) => {
      console.log(`Message ${index} Location:`, msg.location);
    });
  }, [messages]);

  useEffect(() => {
    const intervalId = setInterval(updateLocation, 30000);
    return () => clearInterval(intervalId);
  }, [updateLocation]);

  const fetchMessages = async () => {
    try {
      const response = await fetch('http://localhost:9000/api/messages');
      if (!response.ok) {
        throw new Error('Failed to fetch messages');
      }
      const data = await response.json();
      setMessages(data);
      setLoading(false);
    } catch (error) {
      console.error('Failed to fetch messages:', error);
      setError(error.message);
      setLoading(false);
    }
  };

  const getCurrentLocation = async () => {
    return new Promise((resolve, reject) => {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          (position) => {
            resolve({
              latitude: position.coords.latitude,
              longitude: position.coords.longitude,
            });
          },
          (error) => reject(error)
        );
      } else {
        reject(new Error('Geolocation is not supported by this browser.'));
      }
    });
  };

  const handleKeyDown = async (event) => {
    if (event.key === 'Enter') {
      await sendMessage();
    }
  };

  const sendMessage = async () => {
    if (!message.trim()) return;

    const token = localStorage.getItem('token');
    if (!token) {
      console.error('No token found in localStorage');
      return;
    }

    try {
      const { latitude, longitude } = await getCurrentLocation();
      const response = await fetch('http://localhost:9000/api/messages', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({
          text: message,
          user: user.username,
          location: { latitude, longitude },
        }),
      });

      if (response.ok) {
        const newMessage = await response.json();
        setMessages([...messages, newMessage]);
        setMessage('');
      } else {
        console.error('Failed to send message');
      }
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  const fetchUsersInBoundary = async (latitude, longitude, radius) => {
    try {
      const response = await fetch('http://localhost:9000/api/users/boundary', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          center: { latitude, longitude },
          radius,
        }),
      });

      if (response.ok) {
        const data = await response.json();
        setUsersInBoundary(data.users);
      } else {
        console.error('Failed to fetch users in boundary');
      }
    } catch (error) {
      console.error('Error fetching users in boundary:', error);
    }
  };

  const startFetchingUsersInBoundary = async () => {
    try {
      const { latitude, longitude } = await getCurrentLocation();
      fetchUsersInBoundary(latitude, longitude, 5); // Radius in kilometers
    } catch (error) {
      console.error('Error getting current location:', error);
    }
  };

  useEffect(() => {
    const intervalId = setInterval(startFetchingUsersInBoundary, 10000); // 10 seconds
    return () => clearInterval(intervalId);
  }, []);

  const handleMessageEdit = async (messageId, newMessageText) => {
    const token = localStorage.getItem('token');
    if (!token) {
      console.error('No token found in localStorage');
      return;
    }

    try {
      const response = await fetch(`http://localhost:9000/api/messages/${messageId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({ text: newMessageText }),
      });

      if (response.ok) {
        const updatedMessage = await response.json();
        setMessages((prevMessages) =>
          prevMessages.map((msg) =>
            msg._id === messageId ? updatedMessage : msg
          )
        );
        setEditingMessage(null);
      } else {
        console.error('Failed to update message');
      }
    } catch (error) {
      console.error('Error updating message:', error);
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="home-page">
      <div className="top-section">
        <div className="left-section">
          <MessageListComponent
            messages={messages}
            user={user}
            setMessages={setMessages}
            fetchMessages={fetchMessages}
            handleMessageEdit={handleMessageEdit}
          />
          <div>
            <MessageInputComponent
              message={message}
              setMessage={setMessage}
              handleKeyDown={handleKeyDown}
              sendMessage={sendMessage}
            />
            {editingMessage && (
              <EditMessageComponent
                editingMessage={editingMessage}
                handleMessageEdit={handleMessageEdit}
                setEditingMessage={setEditingMessage}
              />
            )}
          </div>
        </div>
        <div className="right-section">
          <SearchPlaceComponent setDestination={setDestination} />
          {destination && (
            <div>
              <h3>Selected Destination:</h3>
              <p>{destination.formatted_address}</p>
            </div>
          )}
          <div className="users-boundary">
            <h2>Users in Boundary</h2>
            {usersInBoundary.map((user) => (
              <div key={user._id}>
                <p>{user.username}</p>
                <p>Email: {user.email}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
      <div className="bottom-section">
        <LogoutButtonComponent />
      </div>
    </div>
  );
};

export default HomePage;
