import React, { useState, useEffect, useCallback } from 'react';
import '../styles/home.css';
import { useUser } from '../contexts/UserContext'; // Assuming you have a UserContext to provide the logged-in user's info

const HomePage = () => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { user } = useUser(); // Get the current logged-in user

  const updateLocation = useCallback(async () => {
    if (user) {
      try {
        const location = await getCurrentLocation();
        const response = await fetch(`http://localhost:9000/api/users/${user._id}/location`, {
          method: 'PATCH',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            latitude: location.coordinates[1],
            longitude: location.coordinates[0],
          }),
        });

        const data = await response.json();
        if (data.success) {
          console.log('Location updated in database'); // Console log for update
        } else {
          console.error('Failed to update location:', data.error);
        }
      } catch (error) {
        console.error('Error updating location:', error);
      }
    }
  }, [user]);



  useEffect(() => {
    fetchMessages();
    const interval = setInterval(updateLocation, 30000); // Update location every 30 seconds
    return () => clearInterval(interval); // Clear interval on component unmount
  }, [user, updateLocation]);

  const fetchMessages = async () => {
    try {
      const response = await fetch('http://localhost:9000/api/messages');
      if (!response.ok) {
        throw new Error('Failed to fetch messages');
      }
      const data = await response.json();
      setMessages(data.reverse());
      setLoading(false);
    } catch (error) {
      console.error('Failed to fetch messages:', error);
      setError(error.message);
      setLoading(false);
    }
  };

  const handleKeyDown = async (event) => {
    if (event.key === 'Enter') {
      await sendMessage();
    }
  };

  const getCurrentLocation = async () => {
    return new Promise((resolve, reject) => {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          (position) => {
            resolve({
              type: 'Point',
              coordinates: [position.coords.longitude, position.coords.latitude], // Note: Longitude comes first
            });
          },
          (error) => {
            reject(error);
          }
        );
      } else {
        reject(new Error('Geolocation is not supported by this browser.'));
      }
    });
  };

  const sendMessage = async () => {
    if (message.trim() !== '') {
      try {
        const location = await getCurrentLocation();
        const messageObject = {
          text: message,
          location: location,
        };

        const response = await fetch('http://localhost:9000/api/messages/send', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(messageObject),
        });

        const data = await response.json();
        console.log('Success:', data);
        if (data.success) {
          setMessages([data.message, ...messages]);
        }
        setMessage('');
      } catch (error) {
        console.error('Error sending message:', error);
      }
    }
  };


  return (
    <div className="container">
      <h2 className="home-header">Home Page</h2>
      <div className="message-input-container">
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Type your message..."
          onKeyDown={handleKeyDown}
        />
        <button onClick={sendMessage}>Send</button>
      </div>

      <div className="messages-container">
        {loading ? (
          <p className="loading">Loading...</p>
        ) : error ? (
          <p className="error">Error: {error}</p>
        ) : (
          messages.map((msg, index) => (
            <div key={index} className="message">
              <p>{msg.text}</p>
              {msg.location && msg.location.coordinates && (
                <p className="message-location">
                  Location: {msg.location.coordinates[1]}, {msg.location.coordinates[0]}
                </p>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default HomePage;
