import React, { useState, useEffect } from 'react';
import { useUser } from '../contexts/UserContext';
import { useNavigate } from 'react-router-dom';
import '../styles/HomePage.css';

const HomePage = () => {
  const { setUser } = useUser();
  const [user, setUserState] = useState(null);
  const [message, setMessage] = useState('');
  const [image, setImage] = useState(null); // State for storing the selected image
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
    } else {
      fetch('http://localhost:9000/api/auth/validate-token', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      })
        .then(response => response.json())
        .then(data => {
          if (data.success) {
            setUser({ id: data.user.id, username: data.user.username });
            localStorage.setItem('userId', data.user.id);
            setUserState({ id: data.user.id, username: data.user.username });
            setLoading(false);
          } else {
            localStorage.removeItem('token');
            localStorage.removeItem('userId');
            navigate('/login');
          }
        })
        .catch(() => {
          localStorage.removeItem('token');
          localStorage.removeItem('userId');
          navigate('/login');
        });
    }
  }, [navigate, setUser]);

  useEffect(() => {
    fetchMessages();
  }, []);

  const fetchMessages = async () => {
    try {
      const response = await fetch('http://localhost:9000/api/messages');
      if (!response.ok) {
        throw new Error('Failed to fetch messages');
      }
      const data = await response.json();
      setMessages(data);
      setLoading(false);
    } catch (error) {
      console.error('Failed to fetch messages:', error);
      setError(error.message);
      setLoading(false);
    }
  };

  const sendMessage = async () => {
    if (message.trim() !== '' || image) {
      try {
        const location = await getCurrentLocation();
        const formData = new FormData();
        formData.append('text', message);
        formData.append('location', JSON.stringify(location));
        if (image) formData.append('image', image);

        const response = await fetch('http://localhost:9000/api/messages/send', {
          method: 'POST',
          body: formData,
        });

        const data = await response.json();
        if (data.success) {
          setMessages([data.message, ...messages]);
        }
        setMessage('');
        setImage(null);
      } catch (error) {
        console.error('Error sending message:', error);
      }
    }
  };

  const getCurrentLocation = async () => {
    return new Promise((resolve, reject) => {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          (position) => {
            resolve({
              latitude: position.coords.latitude,
              longitude: position.coords.longitude,
            });
          },
          (error) => reject(error)
        );
      } else {
        reject(new Error('Geolocation is not supported by this browser.'));
      }
    });
  };

  const handleKeyDown = async (event) => {
    if (event.key === 'Enter') {
      await sendMessage();
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    navigate('/login');
  };

  return (
    <div className="container">
      <h1>Welcome {user && user.username}</h1>
      <h2 className="home-header">Home Page</h2>
      <div className="message-input-container">
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Type your message..."
          onKeyDown={handleKeyDown}
        />
        <input
          type="file"
          onChange={(e) => setImage(e.target.files[0])} // Handle image selection
        />
        <button onClick={sendMessage}>Send</button>
        <button onClick={handleLogout}>Logout</button>
      </div>
      {loading && <p>Loading...</p>}
      {error && <p>{error}</p>}
      <div className="messages-container">
        {messages.map((msg, index) => (
          <div key={index} className="message">
            <p>{msg.text}</p>
            {msg.imageUrl && (
              <img src={msg.imageUrl} alt="Uploaded" className="message-image" />
            )}
            {msg.location && (
              <p className="message-location">
                Location: Latitude {msg.location.latitude}, Longitude {msg.location.longitude}
              </p>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
