import React, { useState, useEffect, useCallback } from 'react';
import { useUser } from '../contexts/UserContext';
import { useNavigate } from 'react-router-dom';

const HomePage = () => {
  const { setUser } = useUser();
  const [user, setUserState] = useState(null);
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const [usersInBoundary, setUsersInBoundary] = useState([]);
  const [editingMessage, setEditingMessage] = useState(null);

  const getCurrentLocation = async () => {
    return new Promise((resolve, reject) => {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          (position) => {
            resolve({
              latitude: position.coords.latitude,
              longitude: position.coords.longitude,
            });
          },
          (error) => reject(error)
        );
      } else {
        reject(new Error('Geolocation is not supported by this browser.'));
      }
    });
  };

  const fetchUsersInBoundary = async (center, radius) => {
    try {
      const response = await fetch('http://localhost:9000/api/users/boundary', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ center, radius }),
      });
      const data = await response.json();
      if (data.success) {
        setUsersInBoundary(data.users);
      } else {
        console.error('Failed to fetch users in boundary:', data.message);
      }
    } catch (error) {
      console.error('Error fetching users in boundary:', error);
    }
  };

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
    } else {
      fetch('http://localhost:9000/api/auth/validate-token', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      })
        .then(response => response.json())
        .then(data => {
          if (data.success) {
            setUser({ id: data.user.id, username: data.user.username });
            localStorage.setItem('userId', data.user.id);
            setUserState({ id: data.user.id, username: data.user.username });
            setLoading(false);
          } else {
            localStorage.removeItem('token');
            localStorage.removeItem('userId');
            navigate('/login');
          }
        })
        .catch(() => {
          localStorage.removeItem('token');
          localStorage.removeItem('userId');
          navigate('/login');
        });
    }
  }, [navigate, setUser]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    navigate('/login');
  };

  return (
    <div className="container">
      <h1>Welcome {user && user.username}</h1>
      <button onClick={() => fetchUsersInBoundary({ latitude: 40.7128, longitude: -74.0060 }, 10)}>
        Get Users in Boundary
      </button>
      {usersInBoundary.length > 0 ? (
        usersInBoundary.map((user, index) => (
          <div key={index}>
            <p>{user.username}</p>
            <p>{user.email}</p>
          </div>
        ))
      ) : (
        <p>No users found in boundary.</p>
      )}
      <h2 className="home-header">Home Page</h2>
      {/* Add your other components and logic here */}
      <button onClick={handleLogout}>Logout</button>
    </div>
  );
};

export default HomePage;
